hudson.tasks.Shell.command:
#!/bin/bash
echo "########## STARTING STAGE: PROVISION CLOUD RESOURCES ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: PROVISION CLOUD RESOURCES ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct provision remote all-in-one --os "rhel" --stage "base" --provider "aws" --discrete-ssh-config --name "${JOB_NAME}_${BUILD_NUMBER}"

#!/bin/bash
echo "########## STARTING STAGE: SYNC ORIGIN REPOSITORY ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: SYNC ORIGIN REPOSITORY ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct sync remote origin --branch "${ORIGIN_TARGET_BRANCH}"

#!/bin/bash
echo "########## STARTING STAGE: SYNC ORIGIN PULL REQUEST ${ORIGIN_PULL_ID} ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: SYNC ORIGIN PULL REQUEST ${ORIGIN_PULL_ID} ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct sync remote origin --refspec "pull/${ORIGIN_PULL_ID}/head" --branch "pull-${ORIGIN_PULL_ID}" --merge-into "${ORIGIN_TARGET_BRANCH}"

#!/bin/bash
echo "########## STARTING STAGE: SYNC RELEASE REPOSITORY ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: SYNC RELEASE REPOSITORY ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct sync remote release --branch "${RELEASE_TARGET_BRANCH}"

#!/bin/bash
echo "########## STARTING STAGE: BUILD ORIGIN RPMS ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: BUILD ORIGIN RPMS ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
{
cat <<EOF
sudo su origin
set -o errexit -o nounset -o pipefail -o xtrace
cd "\${GOPATH}/src/github.com/openshift/origin"
OS_BUILD_ENV_DOCKER_ARGS="-e OS_VERSION_FILE= " OS_BUILD_ENV_PRESERVE="_output/local/releases/rpms:_output/local/bin" hack/env make build-rpms BUILD_TESTS=1
EOF
} | ssh -F ./.config/origin-ci-tool/inventory/.ssh_config openshiftdevel

#!/bin/bash echo "########## STARTING STAGE: MOVE RPMS TO GCS REPO ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: MOVE RPMS TO GCS REPO ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi if [[ -n "${ORIGIN_PULL_ID-}" ]]; then location_base="origin-ci-test/pr-logs/${ORIGIN_PULL_ID}/${JOB_NAME}/${BUILD_NUMBER}" location="${location_base}/artifacts/rpms" else location_root="origin-ci-test/branch-logs/origin/${ORIGIN_TARGET_BRANCH}/builds" location_base="${location_root}/${BUILD_NUMBER}" location="${location_base}/artifacts/rpms" base_location_url="https://storage.googleapis.com/${location_base}" fi location_url="https://storage.googleapis.com/${location}" echo "${JOB_NAME}/${BUILD_NUMBER}" > .build if gsutil ls "gs://${location_base}" &>/dev/null; then gsutil rm -rf "gs://${location_base}" fi gsutil cp .build "gs://${location_base}/.build"
mkdir -p artifacts/rpms rsync --archive --omit-dir-times --rsh "ssh -F ./.config/origin-ci-tool/inventory/.ssh_config" --rsync-path='sudo rsync' openshiftdevel:/data/src/github.com/openshift/origin/_output/local/releases/rpms ./artifacts/ || true gsutil -m cp -r artifacts/rpms "gs://${location}" if [[ -n "${ORIGIN_PULL_ID:-}" ]]; then # if we've built this PR before, remove older rpm artifact directories out="$( gsutil ls -d "gs://origin-ci-test/pr-logs/${ORIGIN_PULL_ID}/${JOB_NAME}/*/artifacts/rpms" | sort -n -t / -k 7 | head -n -1 )" if [[ -n "${out}" ]]; then echo "${out}" | xargs -L 1 gsutil rm -rf fi else # update the pointer to this location echo "${base_location_url}" > .latest gsutil cp .latest "gs://${location_root}/.latest" fi
# hack around forwarding this to the other machine ssh -F ./.config/origin-ci-tool/inventory/.ssh_config openshiftdevel "echo 'location_url=${location_url:-}' >> /etc/environment"

#!/bin/bash
echo "########## STARTING STAGE: DEPROVISION CLOUD RESOURCES ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: DEPROVISION CLOUD RESOURCES ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct deprovision
