hudson.tasks.Shell.command:
#!/bin/bash
echo "########## STARTING STAGE: PROVISION CLOUD RESOURCES ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: PROVISION CLOUD RESOURCES ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct provision remote all-in-one --os "rhel" --stage "base" --provider "aws" --discrete-ssh-config --name "${JOB_NAME}_${BUILD_NUMBER}"

#!/bin/bash
echo "########## STARTING STAGE: SYNC ORIGIN REPOSITORY ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: SYNC ORIGIN REPOSITORY ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct sync remote origin --branch "${ORIGIN_TARGET_BRANCH}"

#!/bin/bash
echo "########## STARTING STAGE: SYNC ORIGIN PULL REQUEST ${ORIGIN_PULL_ID} ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: SYNC ORIGIN PULL REQUEST ${ORIGIN_PULL_ID} ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct sync remote origin --refspec "pull/${ORIGIN_PULL_ID}/head" --branch "pull-${ORIGIN_PULL_ID}" --merge-into "${ORIGIN_TARGET_BRANCH}"

#!/bin/bash
echo "########## STARTING STAGE: RUN EXTENDED TESTS ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: RUN EXTENDED TESTS ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
{
cat <<EOF
sudo su origin
set -o errexit -o nounset -o pipefail -o xtrace
cd "\${GOPATH}/src/github.com/openshift/origin"
JUNIT_REPORT='true' make test-extended SUITE=networking-minimal
EOF
} | ssh -F ./.config/origin-ci-tool/inventory/.ssh_config openshiftdevel

#!/bin/bash
echo "########## STARTING STAGE: DEPROVISION CLOUD RESOURCES ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; echo "########## FINISHED STAGE: ${status:-SUCCESS}: DEPROVISION CLOUD RESOURCES ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct deprovision
