set -o errexit -o nounset -o pipefail -o xtrace
approve.sh sti-wildfly "${STI_WILDFLY_TARGET_BRANCH}" "${MERGE_SEVERITY}"

#!/bin/bash
SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: PROVISION CLOUD RESOURCES ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: PROVISION CLOUD RESOURCES [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct provision remote all-in-one --os "rhel" --stage "base" --provider "aws" --discrete-ssh-config --name "${JOB_NAME}_${BUILD_NUMBER}"

#!/bin/bash
SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: SYNC STI-WILDFLY REPOSITORY ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: SYNC STI-WILDFLY REPOSITORY [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct sync remote sti-wildfly --branch "${STI_WILDFLY_TARGET_BRANCH}"

#!/bin/bash SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: SYNC STI-WILDFLY PULL REQUEST ${PULL_NUMBER:-}${STI_WILDFLY_PULL_ID:-} ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: SYNC STI-WILDFLY PULL REQUEST ${PULL_NUMBER:-}${STI_WILDFLY_PULL_ID:-} [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi cat << SCRIPT > unravel-pull-refs.py #!/usr/bin/env python from __future__ import print_function import sys
# PULL_REFS is expected to be in the form of: # # base_branch:commit_sha_of_base_branch,pull_request_no:commit_sha_of_pull_request_no,... # # For example: # # master:97d901d,4:bcb00a1 # # And for multiple pull requests that have been batched: # # master:97d901d,4:bcb00a1,6:ddk2tka print( "\n".join([r.split(':')[0] for r in sys.argv[1].split(',')][1:]) ) SCRIPT chmod +x unravel-pull-refs.py
if [[ -n "${PULL_REFS:-}" ]]; then for ref in $(./unravel-pull-refs.py $PULL_REFS); do oct sync remote sti-wildfly --refspec "pull/$ref/head" --branch "pull-$ref" --merge-into "${PULL_REFS%%:*}" done elif [[ -n "${STI_WILDFLY_PULL_ID:-}" ]]; then oct sync remote sti-wildfly --refspec "pull/${STI_WILDFLY_PULL_ID}/head" --branch "pull-${STI_WILDFLY_PULL_ID}" --merge-into "${STI_WILDFLY_TARGET_BRANCH}" else echo "[ERROR] Either \$PULL_REFS or ${STI_WILDFLY_PULL_ID:-} and \$STI_WILDFLY_TARGET_BRANCH must be set" exit 1 fi

