#!/bin/bash SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: RUN THE ACS-ENGINE E2E TEST ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: RUN THE ACS-ENGINE E2E TEST [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi script="$( mktemp )" cat <<SCRIPT >"${script}" #!/bin/bash set -o errexit -o nounset -o pipefail -o xtrace cd "\${HOME}" set -a +o xtrace . /data/src/github.com/openshift/release/cluster/test-deploy/azure/credentials set +a -o xtrace
cd /data/src/github.com/Azure/acs-engine export GOPATH="/data" export PATH="\${PATH}:\${GOPATH}/bin" make build
set +o xtrace docker run --rm -v \${PWD}:/go/src/github.com/Azure/acs-engine:z \ -w /go/src/github.com/Azure/acs-engine \ -e ORCHESTRATOR=openshift \ -e ORCHESTRATOR_VERSION=unstable \ -e IMAGE_RESOURCE_GROUP=\$RESOURCE_GROUP \ -e IMAGE_NAME=centos7-origin \ -e CLUSTER_DEFINITION=examples/openshift.json \ -e TIMEOUT=30m \ -e CLEANUP_ON_EXIT=true \ -e REGIONS=eastus \ -e CLIENT_ID=\$AZURE_CLIENT_ID \ -e CLIENT_SECRET=\$AZURE_SECRET \ -e TENANT_ID=\$AZURE_TENANT \ -e SUBSCRIPTION_ID=\$AZURE_SUBSCRIPTION_ID \ registry.svc.ci.openshift.org/ci/acs-engine-tests:v3.9 make test-openshift set -o xtrace SCRIPT chmod +x "${script}" scp -F ${WORKSPACE}/.config/origin-ci-tool/inventory/.ssh_config "${script}" openshiftdevel:"${script}" ssh -F ${WORKSPACE}/.config/origin-ci-tool/inventory/.ssh_config -t openshiftdevel "bash -l -c \"timeout 14400 ${script}\""

