hudson.tasks.Shell.command:
#!/bin/bash
SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: PROVISION CLOUD RESOURCES ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: PROVISION CLOUD RESOURCES [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct provision remote all-in-one --os "fedora" --stage "crio" --provider "aws" --discrete-ssh-config --name "${JOB_NAME}_${BUILD_NUMBER}"

#!/bin/bash SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: UPLOAD GCS STARTING METADATA ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: UPLOAD GCS STARTING METADATA [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi trap 'exit 0' EXIT mkdir -p gcs/ if [[ -n "${REPO_OWNER:-}" ]]; then cat <<STARTED >gcs/started.json { "timestamp": $( date +%s ), "pull": "${PULL_REFS}", "repos": { "${REPO_OWNER}/${REPO_NAME}": "${PULL_REFS}" } } STARTED else cat <<STARTED >gcs/started.json { "timestamp": $( date +%s ) } STARTED fi
bucket="gs://origin-federated-results/" suffix="${JOB_NAME}/${BUILD_NUMBER}/" if [[ -n "${REPO_OWNER:-}" ]]; then segment="${REPO_OWNER}_${REPO_NAME}/" if [[ -n "${PULL_NUMBER:-}" ]]; then prefix="pr-logs/pull/" segment="${segment:-}${PULL_NUMBER}/" else if [[ "${PULL_REFS}" =~ ^[^:]+:[^:]+(,[^:]+:[^:]+){2,} ]]; then prefix="pr-logs/pull/batch/" else prefix="logs/" fi fi else prefix="logs/" fi
path="${bucket}${prefix}${segment:-}${suffix}" gsutil cp gcs/started.json "${path}started.json" if [[ -n "${PULL_NUMBER:-}" ]]; then echo "${path%\/}" > "${BUILD_NUMBER}.txt" gsutil cp "${BUILD_NUMBER}.txt" "gs://origin-federated-results/pr-logs/directory/${JOB_NAME}/${BUILD_NUMBER}.txt" fi

#!/bin/bash
SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: DEPROVISION CLOUD RESOURCES ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: DEPROVISION CLOUD RESOURCES [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi
oct deprovision
