#!/bin/bash SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: UPLOAD GCS STARTING METADATA ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: UPLOAD GCS STARTING METADATA [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi trap 'exit 0' EXIT mkdir -p gcs/ scp -F ./.config/origin-ci-tool/inventory/.ssh_config openshiftdevel:/data/started.json gcs/
function gcs_path() { bucket="gs://origin-ci-test/"
if [[ -z "${buildId:-}" ]]; then echo "Not a Prow job!" 1>&2 return 1 fi suffix="${JOB_NAME}/${BUILD_NUMBER}/" if [[ -n "${REPO_OWNER:-}" ]]; then if [[ "${REPO_NAME}" != "origin" ]]; then segment="${REPO_OWNER}_${REPO_NAME}/" fi if [[ -n "${PULL_NUMBER:-}" ]]; then # this is a presubmit prefix="pr-logs/pull/" segment="${segment:-}${PULL_NUMBER}/" else if [[ "${PULL_REFS}" =~ ^[^:]+:[^:]+(,[^:]+:[^:]+){2,} ]]; then # this is a batch prefix="pr-logs/pull/batch/" else # this is a postsubmit prefix="logs/" fi fi else # this is a periodic prefix="logs/" fi echo "${bucket}${prefix}${segment:-}${suffix}" return 0 } if path="$( gcs_path )"; then gsutil cp gcs/started.json "${path}started.json" if [[ -n "${PULL_NUMBER:-}" ]]; then echo "${path%\/}" > "${BUILD_NUMBER}.txt" gsutil cp "${BUILD_NUMBER}.txt" "gs://origin-ci-test/pr-logs/directory/${JOB_NAME}/${BUILD_NUMBER}.txt" fi fi

#!/bin/bash SCRIPT_START_TIME="$( date +%s )" && export SCRIPT_START_TIME && echo "########## STARTING STAGE: ASSEMBLE GCS OUTPUT ##########" && trap 'export status=FAILURE' ERR && trap 'set +o xtrace; SCRIPT_END_TIME="$( date +%s )"; ELAPSED_TIME="$(( SCRIPT_END_TIME - SCRIPT_START_TIME ))"; echo "########## FINISHED STAGE: ${status:-SUCCESS}: ASSEMBLE GCS OUTPUT [$( printf "%02dh %02dm %02ds" "$(( ELAPSED_TIME/3600 ))" "$(( (ELAPSED_TIME%3600)/60 ))" "$(( ELAPSED_TIME%60 ))" )] ##########"' EXIT && set -o errexit -o nounset -o pipefail -o xtrace && if [[ -s "${WORKSPACE}/activate" ]]; then source "${WORKSPACE}/activate"; fi trap 'exit 0' EXIT mkdir -p gcs/artifacts gcs/artifacts/generated gcs/artifacts/journals gcs/artifacts/gathered scp -F ./.config/origin-ci-tool/inventory/.ssh_config openshiftdevel:/data/finished.json gcs/ cat "/var/lib/jenkins/jobs/${JOB_NAME}/builds/${BUILD_NUMBER}/log" > gcs/build-log.txt cp artifacts/generated/* gcs/artifacts/generated/ cp artifacts/journals/* gcs/artifacts/journals/ cp -r artifacts/gathered/* gcs/artifacts/
function gcs_path() { bucket="gs://origin-ci-test/"
if [[ -z "${buildId}" ]]; then echo "Not a Prow job!" 1>&2 return 1 fi suffix="${JOB_NAME}/${BUILD_NUMBER}/" if [[ -n "${REPO_OWNER:-}" ]]; then if [[ "${REPO_NAME:-}" != "origin" ]]; then segment="${REPO_OWNER}_${REPO_NAME}/" fi if [[ -n "${PULL_NUMBER:-}" ]]; then # this is a presubmit prefix="pr-logs/pull/" segment="${segment:-}${PULL_NUMBER}/" else if [[ "${PULL_REFS}" =~ ^[^:]+:[^:]+(,[^:]+:[^:]+){2,} ]]; then # this is a batch prefix="pr-logs/pull/batch/" else # this is a postsubmit prefix="logs/" fi fi else # this is a periodic prefix="logs/" fi echo "${bucket}${prefix}${segment:-}${suffix}" return 0 } if path="$( gcs_path )"; then gsutil -m cp -r gcs/* "${path}" fi

